sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();

	// Query the database for NeedIt Task records with Due date field values between current time
	// and 24 hours from then.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var withinDayNITasks = new GlideRecord('x_58872_needit_needit_task');
	withinDayNITasks.addQuery('due_date','>=',rightNow);
	withinDayNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
	withinDayNITasks.addQuery('state','!=',3);

	withinDayNITasks.query();
	// Write a log message for each NeedIt Task Record due within 24 hours
	while(withinDayNITasks.next()){
		gs.info("NeedIt Task due soon record = " + withinDayNITasks.number);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',withinDayNITasks,withinDayNITasks.number,gs.getUserName());
	}
